import React, { useState } from 'react';
import styled from 'styled-components';
import axios from 'axios';
import CalendarComponent from './Calendar';

// Styled components for layout and styling
const AppContainer = styled.div`
   font-family: Arial, sans-serif;
   display: flex;
   flex-direction: column;
   align-items: center;
   padding: 20px;
   background-color: #f5f5f5;
   min-height: 100vh;
`;

const Title = styled.h1`
   color: #333;
   margin-bottom: 20px;
`;

const Textarea = styled.textarea`
   width: 90%;
   max-width: 500px;
   height: 100px;
   padding: 10px;
   font-size: 1em;
   margin-top: 10px;
   border-radius: 5px;
   border: 1px solid #ccc;
   resize: none;
`;

const Button = styled.button`
   margin: 15px 0;
   padding: 10px 20px;
   background-color: #4caf50;
   color: #fff;
   border: none;
   border-radius: 5px;
   cursor: pointer;
   font-size: 1em;
   &:hover {
      background-color: #45a049;
   }
`;

const Input = styled.input`
   width: 90%;
   max-width: 400px;
   padding: 10px;
   margin-top: 10px;
   font-size: 1em;
   border-radius: 5px;
   border: 1px solid #ccc;
`;

const ProfileContainer = styled.div`
   width: 90%;
   max-width: 500px;
   padding: 20px;
   background-color: #fff;
   border-radius: 5px;
   border: 1px solid #ddd;
   margin-top: 20px;
`;

const SectionTitle = styled.h2`
   color: #444;
   margin-top: 20px;
`;

function App() {
   const [email, setEmail] = useState('');
   const [password, setPassword] = useState('');
   const [username, setUsername] = useState('');
   const [studyHours, setStudyHours] = useState('');
   const [workHours, setWorkHours] = useState('');
   const [preferredTopics, setPreferredTopics] = useState('');
   const [loggedIn, setLoggedIn] = useState(false);
   const [message, setMessage] = useState('');

   // Sign Up
   const handleSignUp = async () => {
      try {
         const response = await axios.post('http://localhost:5000/signup', {
            email,
            password,
            username,
         });
         setMessage(response.data.message);
         setLoggedIn(true); // Auto login after successful sign-up
      } catch (error) {
         setMessage('Error signing up.');
      }
   };

   // Login
   const handleLogin = async () => {
      try {
         const response = await axios.post('http://localhost:5000/login', {
            email,
            password,
         });
         setMessage(response.data.message);
         setLoggedIn(true);
      } catch (error) {
         setMessage('Error logging in.');
      }
   };

   // Update Profile
   const handleUpdateProfile = async () => {
      try {
         const response = await axios.put('http://localhost:5000/profile', {
            studyHours,
            workHours,
            preferredTopics,
         });
         setMessage(response.data.message);
      } catch (error) {
         setMessage('Error updating profile.');
      }
   };

   return (
      <AppContainer>
         <Title>AI-Powered Study Scheduler</Title>

         {/* Conditionally render content based on login status */}
         {!loggedIn ? (
            <>
               <SectionTitle>Sign Up</SectionTitle>
               <Input
                  type="text"
                  placeholder="Username"
                  onChange={(e) => setUsername(e.target.value)}
               />
               <Input
                  type="email"
                  placeholder="Email"
                  onChange={(e) => setEmail(e.target.value)}
               />
               <Input
                  type="password"
                  placeholder="Password"
                  onChange={(e) => setPassword(e.target.value)}
               />
               <Button onClick={handleSignUp}>Sign Up</Button>

               <SectionTitle>Login</SectionTitle>
               <Input
                  type="email"
                  placeholder="Email"
                  onChange={(e) => setEmail(e.target.value)}
               />
               <Input
                  type="password"
                  placeholder="Password"
                  onChange={(e) => setPassword(e.target.value)}
               />
               <Button onClick={handleLogin}>Login</Button>
            </>
         ) : (
            <>
               <ProfileContainer>
                  <SectionTitle>Update Your Profile</SectionTitle>
                  <Input
                     type="number"
                     placeholder="Study Hours"
                     onChange={(e) => setStudyHours(e.target.value)}
                  />
                  <Input
                     type="number"
                     placeholder="Work Hours"
                     onChange={(e) => setWorkHours(e.target.value)}
                  />
                  <Textarea
                     placeholder="Preferred Topics"
                     onChange={(e) => setPreferredTopics(e.target.value)}
                  />
                  <Button onClick={handleUpdateProfile}>Update Profile</Button>
               </ProfileContainer>

               <SectionTitle>Schedule</SectionTitle>
               <CalendarComponent />
            </>
         )}

         {message && <p>{message}</p>}
      </AppContainer>
   );
}

export default App;
