import React, { useState } from 'react';
import styled from 'styled-components';
import axios from 'axios';
import CalendarComponent from './Calendar';

// Styled components for layout and styling
const AppContainer = styled.div`
   font-family: Arial, sans-serif;
   display: flex;
   flex-direction: column;
   align-items: center;
   padding: 20px;
   background-color: #f5f5f5;
   min-height: 100vh;
`;

const Title = styled.h1`
   color: #333;
   margin-bottom: 20px;
`;

const Textarea = styled.textarea`
   width: 90%;
   max-width: 500px;
   height: 100px;
   padding: 10px;
   font-size: 1em;
   margin-top: 10px;
   border-radius: 5px;
   border: 1px solid #ccc;
   resize: none;
`;

const Button = styled.button`
   margin: 15px 0;
   padding: 10px 20px;
   background-color: #4caf50;
   color: #fff;
   border: none;
   border-radius: 5px;
   cursor: pointer;
   font-size: 1em;
   &:hover {
      background-color: #45a049;
   }
`;

const FileInput = styled.input`
   margin-top: 15px;
`;

const SummaryBox = styled.div`
   width: 90%;
   max-width: 500px;
   padding: 15px;
   background-color: #fff;
   border: 1px solid #ddd;
   border-radius: 5px;
   margin-top: 20px;
`;

const SectionTitle = styled.h2`
   color: #444;
   margin-top: 20px;
`;

function App() {
   const [content, setContent] = useState('');
   const [summary, setSummary] = useState('');
   const [flashcards, setFlashcards] = useState([]);

   // Function to summarize text
   const handleSummarize = async () => {
      try {
         const response = await axios.post('http://localhost:5000/summarize', { content });
         setSummary(response.data.summary);
      } catch (error) {
         console.error('Error summarizing content:', error);
      }
   };

   // Function to handle file upload and generate flashcards
   const handleFileUpload = async (event) => {
      const file = event.target.files[0];
      const formData = new FormData();
      formData.append('file', file);

      try {
         const response = await axios.post('http://localhost:5000/upload', formData);
         setFlashcards(response.data.flashcards);
      } catch (error) {
         console.error('Error uploading file:', error);
      }
   };

   return (
      <AppContainer>
         <Title>AI-Powered Study Scheduler</Title>

         <SectionTitle>Summarize Study Material</SectionTitle>
         <Textarea
            value={content}
            onChange={(e) => setContent(e.target.value)}
            placeholder="Paste your study material here"
         />
         <Button onClick={handleSummarize}>Summarize</Button>
         {summary && (
            <SummaryBox>
               <h3>Summary</h3>
               <p>{summary}</p>
            </SummaryBox>
         )}

         <SectionTitle>Upload Study Material</SectionTitle>
         <FileInput type="file" onChange={handleFileUpload} />
         {flashcards.length > 0 && (
            <SummaryBox>
               <h3>Generated Flashcards</h3>
               <ul>
                  {flashcards.map((card, index) => (
                     <li key={index}>
                        <strong>Q:</strong> {card.question} <br />
                        <strong>A:</strong> {card.answer}
                     </li>
                  ))}
               </ul>
            </SummaryBox>
         )}

         <SectionTitle>Schedule</SectionTitle>
         <CalendarComponent />
      </AppContainer>
   );
}

export default App;
